INSERT INTO `wp_posts` (`ID`, `post_author`, `post_date`, `post_date_gmt`, `post_content`, `post_title`, `post_excerpt`, `post_status`, `comment_status`, `ping_status`, `post_password`, `post_name`, `to_ping`, `pinged`, `post_modified`, `post_modified_gmt`, `post_content_filtered`, `post_parent`, `guid`, `menu_order`, `post_type`, `post_mime_type`, `comment_count`) VALUES
(1, 1, '2017-11-03 10:20:51', '2017-11-03 10:20:51', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!\r\n\r\n<i class=\"fa fa-camera-retro fa-lg\"></i>', 'Hello world!', '', 'publish', 'open', 'open', '', 'hello-world', '', '', '2017-11-04 12:39:53', '2017-11-04 12:39:53', '', 0, 'http://localhost/wordpress/?p=1', 0, 'post', '', 1),
(2, 1, '2017-11-03 10:20:51', '2017-11-03 10:20:51', 'This is an example page. It\'s different from a blog post because it will stay in one place and will show up in your site navigation (in most themes). Most people start with an About page that introduces them to potential site visitors. It might say something like this:\n\n<blockquote>Hi there! I\'m a bike messenger by day, aspiring actor by night, and this is my website. I live in Los Angeles, have a great dog named Jack, and I like pi&#241;a coladas. (And gettin\' caught in the rain.)</blockquote>\n\n...or something like this:\n\n<blockquote>The XYZ Doohickey Company was founded in 1971, and has been providing quality doohickeys to the public ever since. Located in Leicester City, XYZ employs over 2,000 people and does all kinds of awesome things for the Leicester community.</blockquote>\n\nAs a new WordPress user, you should go to <a href=\"http://localhost/wordpress/wp-admin/\">your dashboard</a> to delete this page and create new pages for your content. Have fun!', 'Sample Page', '', 'publish', 'closed', 'open', '', 'sample-page', '', '', '2017-11-03 10:20:51', '2017-11-03 10:20:51', '', 0, 'http://localhost/wordpress/?page_id=2', 0, 'page', '', 0),
(1404, 1, '2017-11-11 16:12:41', '2017-11-11 16:12:41', 'Note that you will probably want to test this page in conjunction with the <strong>Blog</strong> page.\r\n\r\nTo test this page:\r\n<ul>\r\n	<li>Navigate to <strong><em>Settings &gt; Reading</em></strong> in the WordPress admin.</li>\r\n	<li>Then use the settings in the screenshot below.</li>\r\n</ul>\r\n<img class=\"alignnone size-full wp-image-1071\" alt=\"Static Pages\" src=\"http://localhost/wordpress/wp-content/uploads/2013/03/static-pages.png\" width=\"1390\" height=\"1022\" />\r\n\r\nA few things to check once this is done.\r\n<ul>\r\n	<li>Navigating to the root URL loads this page.</li>\r\n	<li>The site title should be an <code>H1</code> and the page title should be an <code>H2</code> (if displayed).</li>\r\n	<li>Check that the browser window / tab name is what you intend it to show.</li>\r\n	<li>If your theme displays breadcrumbs, they should probably not show when this page is the front page.</li>\r\n</ul>', 'Home', '', 'inherit', 'closed', 'closed', '', '1064-revision-v1', '', '', '2017-11-11 16:12:41', '2017-11-11 16:12:41', '', 1064, 'http://localhost/wordpress/2017/11/11/1064-revision-v1/', 0, 'revision', '', 0),
(1363, 1, '2017-11-04 12:39:53', '2017-11-04 12:39:53', 'Welcome to WordPress. This is your first post. Edit or delete it, then start writing!\r\n\r\n<i class=\"fa fa-camera-retro fa-lg\"></i>', 'Hello world!', '', 'inherit', 'closed', 'closed', '', '1-revision-v1', '', '', '2017-11-04 12:39:53', '2017-11-04 12:39:53', '', 1, 'http://localhost/wordpress/2017/11/04/1-revision-v1/', 0, 'revision', '', 0),
(1364, 1, '2017-11-04 12:52:29', '2017-11-04 12:52:29', ' ', '', '', 'publish', 'closed', 'closed', '', '1364', '', '', '2017-11-05 13:09:21', '2017-11-05 13:09:21', '', 0, 'http://localhost/wordpress/?p=1364', 1, 'nav_menu_item', '', 0),
(611, 2, '2011-01-10 06:17:54', '2011-01-10 13:17:54', '', 'canola2', 'Lorem ipsum dolor sit amet, consectetuer adipiscing elit. Donec mollis. Quisque convallis libero in sapien pharetra tincidunt. Aliquam elit ante, malesuada id, tempor eu, gravida id, odio. Maecenas suscipit, risus et eleifend imperdiet, nisi orci ullamcorper massa, et adipiscing orci velit quis magna.', 'inherit', 'open', 'closed', '', 'canola2', '', '', '2011-01-10 06:17:54', '2011-01-10 13:17:54', '', 555, 'http://localhost/wordpress/wp-content/uploads/2011/01/canola2.jpg', 0, 'attachment', 'image/jpeg', 0),
